<template>
  <div class="wrapper">
    <div class="item">
      <h1 class="item__title">Task: {{ task.title }}</h1>
    </div>
    <div class="item__content">
      <div>
        <p class="item__subtitle">Description:</p>
        <p>{{ task.description }}</p>
      </div>
      <div>
        <p class="item__status">Status:</p>
        <p>{{ task.done ? 'Done' : 'Not done' }}</p>
      </div>
      <div>
        <p class="item__date">Date:</p>
        <p>{{ task.date }}</p>
      </div>
    </div>
    <div class="item__buttons">
      <button class="item__update-button btn">Update</button>
      <button class="item__delete-button btn">Delete <span class="btn-icon"></span></button>
    </div>
  </div>
</template>

<script>
import { db } from '@/firebase'
import { doc, getDoc } from 'firebase/firestore'

export default {
  data() {
    return {
      id: this.$route.params['id'],
      task: {}
    }
  },
  async mounted() {
    const docRef = doc(db, 'todos', `${this.id}`)
    const docSnap = await getDoc(docRef)

    if (docSnap.exists()) {
      console.log('Document data:', docSnap.data())
      this.task = {
        id: this.id,
        title: docSnap.data().title,
        description: docSnap.data().description,
        done: docSnap.data().done,
        date: docSnap
          .data()
          .date.toDate()
          .toISOString()
          .split('T')[0]
          .split('-')
          .reverse()
          .join('-')
      }
    } else {
      console.log('No such document!')
    }
  }
}
</script>

